@file:Suppress("unused")package com.kafka.baseimport co.touchlab.kermit.Logger/** Delegates the provided message to [Timber.e] if any trees are planted. */inline fun errorLog(throwable: Throwable? = null, message: () -> String = { "" }) =    Logger.e(throwable = throwable, message = message)/** Delegates the provided message to [Timber.w] if any trees are planted. */inline fun w(throwable: Throwable? = null, message: () -> String) =    Logger.w(throwable = throwable, message = message)/** Delegates the provided message to [Timber.i] if any trees are planted. */inline fun i(throwable: Throwable? = null, message: () -> String) =    Logger.i(throwable = throwable, message = message)/** Delegates the provided message to [Timber.d] if any trees are planted. */inline fun debug(throwable: Throwable? = null, message: () -> String) =    Logger.d(throwable = throwable, message = message)/** Delegates the provided message to [Timber.v] if any trees are planted. */inline fun v(throwable: Throwable? = null, message: () -> String) =    Logger.v(throwable = throwable, message = message)/** Delegates the provided message to [Timber.w] if any trees are planted. */inline fun wtf(throwable: Throwable? = null, message: () -> String) =    Logger.a(throwable = throwable, message = message)